	ngOnInit(): void {
		this.createMap()
		this.addTileLayers()
		this.addTatarstanBorder()
		this.addTatarstan1Border()
		this.addLandmarks()
		this.closeVideo()
		const uniqueCategories = new Set(
			this.land.map((item) => item[3].join(', '))
		)
		this.categories = Array.from(uniqueCategories)
		this.toggleFilter()
	}
	getVideoUrl(landmarkId: number): void {
		this.landmarkService
			.getVideoUrl(landmarkId)
			.pipe(
				catchError((error) => {
					console.error(error)
					return 'Не удалось получить ссылку на видео.'
				})
			)
			.subscribe((videoUrl) => {
				this.videoUrl = videoUrl
			})
	}

	private createMap(): void {
		this.map = L.map('map').setView([55.796037, 49.105486], 8)
	}

	private addTileLayers(): void {
		const osm = L.tileLayer(
			'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
			{
				maxZoom: 17,
				minZoom: 7,
				attribution:
					'&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
			}
		)

		const googleHybrid = L.tileLayer(
			'http://{s}.google.com/vt/lyrs=s,h&x={x}&y={y}&z={z}',
			{
				maxZoom: 20,
				subdomains: ['mt0', 'mt1', 'mt2', 'mt3'],
				attribution:
					'&copy; <a href="https://www.google.com/permissions/geoguidelines/">Google</a>',
			}
		)

		const baseMaps = {
			OpenStreetMap: osm,
			'Google Hybrid': googleHybrid,
		}

		L.control.layers(baseMaps).addTo(this.map)

		// установить слой тайлов по умолчанию
		osm.addTo(this.map)
	}

	toggleFilter() {
		this.showFilter = !this.showFilter
	}
	filterLandmarks(category: string) {
		// Если выбранная категория "Все", показать все маркеры
		if (category === 'Все') {
			this.markers.forEach((marker) => {
				marker.setOpacity(1)
			})
		} else {
			// Скрыть маркеры, которые не относятся к выбранной категории
			this.markers.forEach((marker) => {
				const landmark = marker.get('landmark')
				if (landmark[7] !== category) {
					marker.setOpacity(0)
				}
			})

			// Показать маркеры, которые относятся к выбранной категории, но были скрыты ранее
			this.land.forEach((land) => {
				if (land[7] === category) {
					const marker = this.markers.find(
						(marker) => marker.get('landmark') === land
					)
					if (marker) {
						marker.setOpacity(1)
					}
				}
			})
		}
	}

